-- Records of wz
-- ----------------------------
INSERT INTO `wz` VALUES (1, '#保养大肠经的最佳时间', '#大肠经位于上肢外侧，是多气多血之经，阳气最盛，平日可用刮痧、敲打等方法对其进行刺激，可以清除体内的热毒，清洁血液通道，预防青春痘、暗疮等皮肤病。可以沿着大肠经的循行路线进行拍打，每天拍打1次，每次以12分钟为宜，不妨双手交替进行。*卯时（05：00～07：00），大肠经最旺，大肠蠕动，排出毒物渣滓。肺与大肠相表里。肺将充足的新鲜血液布满全身，紧接着促使大肠进入兴奋状态，完成吸收食物中的水分和营养、排出渣滓的过程。清晨起床后最好养成排便的习惯。起床后宜先喝杯温开水，然后去卫生间把一天积攒下来的废物排出体外。晨起一杯温水，可稀释血液，有防止血栓形成的作用。', '#images/banner_sheji1.jpg', '#images/bofang.png#');
INSERT INTO `wz` VALUES (2, '#保养肺经的最佳时间', '#肺经位于上肢内侧，平常看电视、等车等空闲时间都可以用手掌拍一拍该经所循行的位置，不过力度一定要轻。因为人的肺气永远都不会多，只会变少，因此轻度拍打是补气，而用力过重的话，就会“泻”气。每次轻轻拍打1～3分钟即可。*《黄帝内经》中说，寅时（03：00～05：00）经脉气血循行流注至肺经，肺有病的人经常会在此时醒来，这是气血不足的表现。保养肺经此时按摩最好，但此时正是早上睡眠的时间。因此，可在同名经，也就是足太阴脾经当令的时段（09：00～11：00），对肺经和脾经进行按摩。', '#images/banner_sheji1.jpg', '#images/bofang.png#');
